from shutil import move
from os import system, path, mkdir, remove
from requests import get
from time import sleep
from json import load


def get_installer(version):
    print("getting fabric api 1")
    api1 = get(f"https://meta.fabricmc.net/v2/versions/installer")
    print("getting fabric api 2")
    api2 = get(f"https://meta2.fabricmc.net/v2/versions/installer")
    print("getting fabric game versions")
    versions = get("https://meta.fabricmc.net/v2/versions/game").json()
    print("getting fabric loader versions")
    loader_versions = get("https://meta.fabricmc.net/v2/versions/loader").json()
    if api1.status_code == 200:
        download = api1.json()[0]["url"]
        file = get(download)
        with open(f"installer-{api1.json()[0]['version']}.jar", "wb") as e:
            e.write(file.content)
        version_available = False
        for item in versions:
            if version == item["version"]:
                version_available = True
        if version_available:
            system(
                f"java -jar installer-{api1.json()[0]['version']}.jar client -mcversion {version} -loader {loader_versions[0]['version']}")
        return api1.json()[0]['version'], loader_versions[0]["version"]
    elif api1.status_code != 200 and api2.status_code == 200:
        download = api2.json()[0]["url"]
        file = get(download)
        with open(f"installer-{api2.json()[0]['version']}.jar", "wb") as e:
            e.write(file.content)
        version_available = False
        for item in versions:
            if version == item["version"]:
                version_available = True
        if version_available:
            system(
                f"java -jar installer-{api2.json()[0]['version']}.jar client -mcversion {version} -loader {loader_versions[0]['version']}")
        return api2.json()[0]['version'], loader_versions[0]["version"]
    else:
        print("both apis dont work sorry")
        sleep(10)
        exit()


def get_mods():
    print("getting meteor stats")
    meteor_stats = get("https://meteorclient.com/api/stats").json()
    print("getting multiconnect")
    multiconnect = get(f'https://api.modrinth.com/v2/project/sfEDKnlo/version').json()
    print("getting latest meteor")
    meteor = get("https://meteorclient.com/download?devBuild=latest")
    with open(f"meteor-{meteor_stats['dev_build_mc_version']}.jar", "wb") as e:
        e.write(meteor.content)
    multiconnect_send = ''
    if meteor_stats["dev_build_mc_version"] in multiconnect[0]["game_versions"]:
        file_url = multiconnect[0]['files'][0]['url'].replace(f'https//cdn.modrinth.com/data/sfEDKnlo/versions/',
                                                              f'https://cdn-raw.modrinth.com/data/sfEDKnlo/versions/')
        with get(file_url, stream=True) as r:
            with open(multiconnect[0]['files'][0]['filename'], 'wb') as f:
                f.write(r.content)
        multiconnect_send = multiconnect[0]['files'][0]['filename']
    return meteor_stats["dev_build_mc_version"], multiconnect_send


def files(installer_version, multiconnect, version, loader):
    roaming = path.expandvars(r"%APPDATA%")
    mkdir(f"{roaming}\\.minecraft -- meteor")
    mkdir(f"{roaming}\\.minecraft -- meteor\\mods")
    if multiconnect != '':
        move(multiconnect, f"{roaming}\\.minecraft -- meteor\\mods")
    move(f"meteor-{version}.jar", f"{roaming}\\.minecraft -- meteor\\mods")
    with open(f"{roaming}\\.minecraft\\launcher_profiles.json", "r") as e:
        profiles = load(e)
        profiles["profiles"].update({'meteor': {'created': '', 'icon': 'data:image/png;base64,', 'lastUsed': '', 'lastVersionId': f'fabric-loader-{loader}-{version}', 'name': 'meteor', 'type': 'custom', 'gameDir': f'{roaming}\\.minecraft -- meteor'}})
    with open(f"{roaming}\\.minecraft\\launcher_profiles.json", "w") as d:
        d.write(str(profiles).replace("'", '"').replace("True", "true").replace("False", "false"))
    remove(f"installer-{installer_version}.jar")

yes_no = input("warning if you do not have the regular minecraft launcher installed do not run this\ndo you still want to run this yes/no: ")
if yes_no.lower() == "no":
    exit()
if yes_no.lower() == "yes":
    version, multiconnect = get_mods()
    installer_version, loader_version = get_installer(version)
    files(installer_version, multiconnect, version, loader_version)
